import React, { useState, useEffect } from 'react';
import './styles/slideshow.css';

const Slideshow = () => {
  const [currentSlide, setCurrentSlide] = useState(0);

  // Using existing images from the public/imgs directory
  const slides = [
    {
      id: 1,
      image: '/imgs/place1.jpg',
      alt: 'Premium FiveM Clothing Collection',
      title: 'Premium Clothing',
      description: 'Exclusive FiveM clothing designs for your character',
      link: '/clothing'
    },
    {
      id: 2,
      image: '/imgs/place2.jpg',
      alt: 'Luxury FiveM Cars',
      title: 'Luxury Cars',
      description: 'High-performance vehicles for your FiveM experience',
      link: '/cars'
    },
    {
      id: 3,
      image: '/imgs/place3.jpg',
      alt: 'Limited Edition Items',
      title: 'Limited Editions',
      description: 'Special items available for a limited time only',
      link: '/limited'
    }
  ];

  // Auto-advance slides
  useEffect(() => {
    const interval = setInterval(() => {
      setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
    }, 5000);

    return () => clearInterval(interval);
  }, [slides.length]);

  // Manual navigation
  const goToSlide = (index) => {
    setCurrentSlide(index);
  };

  const goToPrevSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide - 1 + slides.length) % slides.length);
  };

  const goToNextSlide = () => {
    setCurrentSlide((prevSlide) => (prevSlide + 1) % slides.length);
  };

  return (
    <div className="slideshow-container">
      <div className="slides">
        {slides.map((slide, index) => (
          <div 
            key={slide.id} 
            className={`slide ${index === currentSlide ? 'active' : ''}`}
          >
            <div className="slide-image-container">
              <img src={slide.image} alt={slide.alt} className="slide-image" />
              <div className="slide-content">
                <h2>{slide.title}</h2>
                <p>{slide.description}</p>
                <a href={slide.link} className="slide-button">Shop Now</a>
              </div>
            </div>
          </div>
        ))}
      </div>

      <button className="slide-arrow prev" onClick={goToPrevSlide}>
        <i className="fas fa-chevron-left"></i>
      </button>
      <button className="slide-arrow next" onClick={goToNextSlide}>
        <i className="fas fa-chevron-right"></i>
      </button>

      <div className="slide-dots">
        {slides.map((_, index) => (
          <button 
            key={index} 
            className={`slide-dot ${index === currentSlide ? 'active' : ''}`}
            onClick={() => goToSlide(index)}
            aria-label={`Go to slide ${index + 1}`}
          />
        ))}
      </div>
    </div>
  );
};

export default Slideshow;
