import './App.css';
import React, { useEffect } from 'react';
import Header from './components/Header';
import ProductShowcase from './components/Slideshow';
import Gallery from './components/Gallery';

function App() {
  useEffect(() => {
    // Add Font Awesome script from a reliable CDN
    const script = document.createElement('script');
    script.src = 'https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.15.4/js/all.min.js';
    script.integrity = 'sha512-Tn2m0TIpgVyTzzvmxLNuqbSJH3JP8jm+Cy3hvHrW7ndTDcJ1w5mBiksqDBb8GpE2ksktFvDB/ykZ0mDpsZj20w==';
    script.crossOrigin = 'anonymous';
    document.body.appendChild(script);

    // Add scroll event listener to force re-render on scroll
    const handleScroll = () => {
      // This is just to ensure React re-renders components that depend on scroll position
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      // Clean up
      document.body.removeChild(script);
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  return (
    <div className="App">
      <Header />
      <ProductShowcase />
      <Gallery />
    </div>
  );
}

export default App;
