import React, { useEffect, useRef, useState } from 'react';
import { Link, useNavigate, useLocation } from 'react-router-dom';
import './styles/styles.css';
import Logo from './Logo';
import Login from './Login';
import Cart from './Cart';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faSun, faMoon, faShoppingCart } from '@fortawesome/free-solid-svg-icons';

const Header = ({ user, onLogin, onLogout, darkMode, toggleDarkMode, children }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const headerRef = useRef(null);
  const [currency, setCurrency] = useState('USD');
  const [isScrolled, setIsScrolled] = useState(false);
  const [showLogin, setShowLogin] = useState(false);
  const isHomePage = location.pathname === '/';

  // Handle currency change
  const handleCurrencyChange = (e) => {
    const newCurrency = e.target.value;
    setCurrency(newCurrency);
    // Store the selected currency in localStorage for persistence
    localStorage.setItem('selectedCurrency', newCurrency);
    // Dispatch a custom event to notify other components about the currency change
    window.dispatchEvent(new CustomEvent('currencyChange', { detail: { currency: newCurrency } }));
  };

  // Handle login button click
  const handleLoginClick = () => {
    setShowLogin(true);
  };

  // Handle account button click
  const handleAccountClick = () => {
    navigate('/account');
  };

  // Handle successful login
  const handleLogin = (userData) => {
    if (onLogin) {
      onLogin(userData);
    }
    setShowLogin(false);
  };

  // Handle logout
  const handleLogout = () => {
    if (onLogout) {
      onLogout();
    }
  };

  // Handle scroll events - simplified to avoid animation issues
  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const scrollThreshold = 100;

      if (scrollPosition > scrollThreshold) {
        setIsScrolled(true);
        if (headerRef.current) {
          headerRef.current.classList.add('scrolled');
        }
      } else {
        setIsScrolled(false);
        if (headerRef.current) {
          headerRef.current.classList.remove('scrolled');
        }
      }
    };

    window.addEventListener('scroll', handleScroll);

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Inline styles to force transparency
  const transparentStyle = {
    background: 'transparent',
    backgroundColor: 'transparent',
    backgroundImage: 'none',
    boxShadow: 'none',
    border: 'none',
    padding: 0,
    margin: 0
  };

  const navLinkStyle = {
    color: '#ffffff',
    textDecoration: 'none',
    padding: '10px 20px',
    display: 'inline-block',
    fontWeight: 600,
    letterSpacing: '0.5px',
    textTransform: 'uppercase',
    fontSize: '14px',
    ...transparentStyle
  };

  return (
    <header className="header" ref={headerRef}>
      {/* Currency Toggle */}
      <div className="currency-toggle">
        <select value={currency} onChange={handleCurrencyChange}>
          <option value="USD">USD ($)</option>
          <option value="EUR">EUR (€)</option>
          <option value="GBP">GBP (£)</option>
          <option value="JPY">JPY (¥)</option>
          <option value="AUD">AUD (A$)</option>
        </select>
      </div>

      {/* Theme Toggle */}
      <div className="theme-toggle">
        <button onClick={toggleDarkMode}>
          <FontAwesomeIcon icon={darkMode ? faSun : faMoon} />
        </button>
      </div>

      {/* Header Controls */}
      <div className="header-controls">
        {user ? (
          <>
            <button onClick={handleAccountClick}>
              <FontAwesomeIcon icon={faUser} />
            </button>
            <button onClick={handleLogout}>
              Logout
            </button>
          </>
        ) : (
          <button onClick={handleLoginClick}>
            <FontAwesomeIcon icon={faUser} />
          </button>
        )}

        <Cart isFixed={false} />
      </div>

      {/* Logo */}
      <div className="logo">
        <Link to="/">
          <Logo />
        </Link>
      </div>

      {/* Search Bar */}
      <div className="header-search">
        {children}
      </div>

      {/* Custom Navbar */}
      <div style={{
        position: 'absolute',
        bottom: 0,
        left: 0,
        width: '100%',
        zIndex: 10,
        ...transparentStyle
      }}>
        <nav style={transparentStyle}>
          <ul style={{
            display: 'flex',
            justifyContent: 'center',
            listStyle: 'none',
            margin: 0,
            padding: 0,
            ...transparentStyle
          }}>
            <li style={transparentStyle}>
              <Link to="/category/clothing" style={navLinkStyle}>Clothing</Link>
            </li>
            <li style={transparentStyle}>
              <Link to="/category/vehicles" style={navLinkStyle}>Vehicles</Link>
            </li>
            <li style={transparentStyle}>
              <Link to="/category/artwork" style={navLinkStyle}>Artwork</Link>
            </li>
            <li style={transparentStyle}>
              <Link to="/category/audio" style={navLinkStyle}>Audio</Link>
            </li>
            <li style={transparentStyle}>
              <Link to="/category/gaming" style={navLinkStyle}>Gaming</Link>
            </li>
            <li style={transparentStyle}>
              <Link to="/category/premium" style={navLinkStyle}>Premium</Link>
            </li>
          </ul>
        </nav>
      </div>

      {/* Login Modal */}
      {showLogin && (
        <Login onLogin={handleLogin} onClose={() => setShowLogin(false)} />
      )}

      {/* Fixed Navbar (shown when scrolled) */}
      <div className={`fixed-navbar ${isScrolled ? 'visible' : ''}`}>
        <div className="fixed-navbar-container">
          <div className="fixed-navbar-logo">
            <Link to="/">
              <Logo />
            </Link>
          </div>

          <div className="fixed-navbar-links">
            <ul>
              <li><Link to="/category/clothing">Clothing</Link></li>
              <li><Link to="/category/vehicles">Vehicles</Link></li>
              <li><Link to="/category/artwork">Artwork</Link></li>
              <li><Link to="/category/audio">Audio</Link></li>
              <li><Link to="/category/gaming">Gaming</Link></li>
              <li><Link to="/category/premium">Premium</Link></li>
            </ul>
          </div>

          <div className="fixed-navbar-controls">
            <select 
              className="fixed-currency-select"
              value={currency} 
              onChange={handleCurrencyChange}
            >
              <option value="USD">USD</option>
              <option value="EUR">EUR</option>
              <option value="GBP">GBP</option>
              <option value="JPY">JPY</option>
              <option value="AUD">AUD</option>
            </select>

            <button 
              className="fixed-theme-toggle"
              onClick={toggleDarkMode}
            >
              <FontAwesomeIcon icon={darkMode ? faSun : faMoon} />
            </button>

            {user ? (
              <button 
                className="fixed-account-btn"
                onClick={handleAccountClick}
              >
                <FontAwesomeIcon icon={faUser} />
              </button>
            ) : (
              <button 
                className="fixed-login-btn"
                onClick={handleLoginClick}
              >
                <FontAwesomeIcon icon={faUser} />
              </button>
            )}

            <Cart isFixed={true} />
          </div>
        </div>
      </div>
    </header>
  );
};

export default Header;
