import './App.css';
import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom';
import Header from './components/Header';
import SearchBar from './components/SearchBar';
import ProductShowcase from './components/Slideshow';
import Gallery from './components/Gallery';
import CategoryBoxes from './components/CategoryBoxes';
import SearchResults from './components/SearchResults';
import { CartProvider } from './context/CartContext';

// Import Font Awesome
import { library } from '@fortawesome/fontawesome-svg-core';
import { fab } from '@fortawesome/free-brands-svg-icons';
import { fas } from '@fortawesome/free-solid-svg-icons';
import { far } from '@fortawesome/free-regular-svg-icons';

// Add all icons to the library
library.add(fab, fas, far);

function App() {
  const [user, setUser] = useState(null);
  const [darkMode, setDarkMode] = useState(true);

  // Check for saved user session on component mount
  useEffect(() => {
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      try {
        setUser(JSON.parse(savedUser));
      } catch (error) {
        console.error('Failed to parse saved user:', error);
        localStorage.removeItem('user');
      }
    }

    // Check for saved dark mode preference
    const savedDarkMode = localStorage.getItem('darkMode');
    if (savedDarkMode !== null) {
      setDarkMode(savedDarkMode === 'true');
    }
  }, []);

  // Toggle dark mode
  const toggleDarkMode = () => {
    const newDarkMode = !darkMode;
    setDarkMode(newDarkMode);
    localStorage.setItem('darkMode', newDarkMode.toString());
    document.body.classList.toggle('dark-mode', newDarkMode);
  };

  // Handle user login
  const handleLogin = (userData) => {
    setUser(userData);
    localStorage.setItem('user', JSON.stringify(userData));
  };

  // Handle user logout
  const handleLogout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };

  return (
    <CartProvider>
      <Router>
        <div className={`app-container ${darkMode ? 'dark-mode' : 'light-mode'}`}>
          <Header 
            user={user} 
            onLogin={handleLogin} 
            onLogout={handleLogout} 
            darkMode={darkMode} 
            toggleDarkMode={toggleDarkMode}
          >
            <SearchBar />
          </Header>

          <div className="main-content">
            <Routes>
              <Route path="/" element={
                <main>
                  <div className="hero-section">
                    <ProductShowcase />
                  </div>
                  <div className="categories-section">
                    <CategoryBoxes />
                  </div>
                  <div className="featured-section">
                    <h2 className="section-title">Featured Items</h2>
                    <Gallery />
                  </div>
                </main>
              } />
              <Route path="/account" element={
                user ? <div className="account-page">
                  <h1>Account Page</h1>
                  <p>Welcome, {user.username || user.email}!</p>
                </div> : <Navigate to="/login" />
              } />
              <Route path="/login" element={
                user ? <Navigate to="/account" /> : <div className="login-page">
                  <h1>Login Page</h1>
                  <p>Please log in to continue.</p>
                </div>
              } />
              <Route path="/product/:id" element={<div className="product-page">
                <h1>Product Details</h1>
              </div>} />
              <Route path="/category/:category" element={<div className="category-page">
                <h1>Category Page</h1>
                <Gallery />
              </div>} />
              <Route path="/search" element={<SearchResults />} />
              <Route path="/checkout" element={<div className="checkout-page">
                <h1>Checkout</h1>
              </div>} />
            </Routes>

            <footer className="main-footer">
              <div className="footer-content">
                <div className="footer-section">
                  <h3>ACYD Gallery</h3>
                  <p>Your premier destination for digital assets and designs.</p>
                </div>
                <div className="footer-section">
                  <h3>Quick Links</h3>
                  <ul>
                    <li><a href="/category/clothing">Clothing</a></li>
                    <li><a href="/category/vehicles">Vehicles</a></li>
                    <li><a href="/category/artwork">Artwork</a></li>
                  </ul>
                </div>
                <div className="footer-section">
                  <h3>Contact</h3>
                  <p>Email: support@acydgallery.com</p>
                  <p>Discord: discord.gg/acydgallery</p>
                </div>
              </div>
              <div className="footer-bottom">
                <p>&copy; {new Date().getFullYear()} ACYD Gallery. All rights reserved.</p>
              </div>
            </footer>
          </div>
        </div>
      </Router>
    </CartProvider>
  );
}

export default App;
