import React, { useEffect, useRef, useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faUser, faSun, faMoon } from '@fortawesome/free-solid-svg-icons';
import { useDispatch, useSelector } from 'react-redux';
import { setCurrency } from '../redux/actions/currencyActions';
import { toggleDarkMode } from '../redux/actions/themeActions';
import { handleLogin, handleLogout } from '../redux/actions/authActions';
import { showLogin } from '../redux/actions/loginActions';
import Cart from './Cart';
import Logo from './Logo';
import Login from './Login';

const Header = ({ children }) => {
  const dispatch = useDispatch();
  const headerRef = useRef(null);
  const location = useLocation(); // Keep this but use it appropriately if needed
  const [isScrolled, setIsScrolled] = useState(false);
  const darkMode = useSelector(state => state.theme.darkMode);
  const user = useSelector(state => state.auth.user);
  const showLogin = useSelector(state => state.login.showLogin);
  const currency = useSelector(state => state.currency.currency);

  // Handle scroll events
  useEffect(() => {
    const handleScroll = () => {
      const scrollPosition = window.scrollY;
      const scrollThreshold = 50; // Reduced threshold for quicker transition

      if (scrollPosition > scrollThreshold && !isScrolled) {
        setIsScrolled(true);
        if (headerRef.current) {
          headerRef.current.classList.add('scrolled');
        }
      } else if (scrollPosition <= scrollThreshold && isScrolled) {
        setIsScrolled(false);
        if (headerRef.current) {
          headerRef.current.classList.remove('scrolled');
        }
      }
    };

    window.addEventListener('scroll', handleScroll);
    handleScroll(); // Call once on mount to set initial state

    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, [isScrolled]);

  const handleCurrencyChange = (e) => {
    dispatch(setCurrency(e.target.value));
  };

  const toggleDarkModeHandler = () => {
    dispatch(toggleDarkMode());
  };

  const handleAccountClick = () => {
    // Implement account click logic
  };

  const handleLoginClick = () => {
    dispatch(showLogin());
  };

  const handleLoginResponse = (user) => {
    dispatch(handleLogin(user));
  };

  return (
    <header className={`header ${isScrolled ? 'scrolled' : ''}`} ref={headerRef}>
      {/* Currency Toggle */}
      <div className="currency-toggle">
        <select value={currency} onChange={handleCurrencyChange}>
          <option value="USD">USD ($)</option>
          <option value="EUR">EUR (€)</option>
          <option value="GBP">GBP (£)</option>
          <option value="JPY">JPY (¥)</option>
          <option value="AUD">AUD (A$)</option>
        </select>
      </div>

      {/* Theme Toggle */}
      <div className="theme-toggle">
        <button onClick={toggleDarkModeHandler}>
          <FontAwesomeIcon icon={darkMode ? faSun : faMoon} />
        </button>
      </div>

      {/* Header Controls */}
      <div className="header-controls">
        {user ? (
          <>
            <button className="account-btn" onClick={handleAccountClick}>
              <FontAwesomeIcon icon={faUser} />
              <span>Account</span>
            </button>
            <button className="logout-btn" onClick={handleLogout}>
              Logout
            </button>
          </>
        ) : (
          <button className="login-btn" onClick={handleLoginClick}>
            <FontAwesomeIcon icon={faUser} />
            <span>Login</span>
          </button>
        )}

        <Cart isFixed={false} />
      </div>

      {/* Logo */}
      <div className="logo">
        <Link to="/">
          <img className='acydgallery' src={'/imgs/invertedacyd-removebg-preview.png'} alt="ACYD Gallery Logo" />
        </Link>
      </div>

      {/* Search Bar */}
      <div className="header-search">
        {children}
      </div>

      {/* Transparent Navbar */}
      <nav className="navbar-center">
        <ul>
          <li><Link to="/category/clothing">Clothing</Link></li>
          <li><Link to="/category/vehicles">Vehicles</Link></li>
          <li><Link to="/category/artwork">Artwork</Link></li>
          <li><Link to="/category/audio">Audio</Link></li>
          <li><Link to="/category/gaming">Gaming</Link></li>
          <li><Link to="/category/premium">Premium</Link></li>
        </ul>
      </nav>

      {/* Login Modal */}
      {showLogin && (
        <Login onLogin={handleLoginResponse} onClose={() => setShowLogin(false)} />
      )}
    </header>
  );
};

export default Header;
